import React from 'react';
import logo from "./memriio_logo.png"


class Navigation extends React.Component{

    constructor(props){
        super(props);
        this.state ={
            searchwords:''
        }
    }

//---------------------------------------------------------------------------------    

    onSearchBoxChange = (event) =>{
        console.log('navigation-onSearchBoxChange : ' + event.target.value)
        let tmparray = []

        tmparray = event.target.value.split(" ")
        console.log(event[1])

        //this.searchMemories(tmparray)
        //this.setState({searchwords:tmparray})        
    }

//---------------------------------------------------------------------------------


    searchMemories = (searchwords) => {

    console.log('navigation-searchMemroies ' + searchwords)

        if(searchwords !== ""){
            fetch('https://memriio-api-0.herokuapp.com/get_memories_keywords_user', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body:JSON.stringify({
                    words:'%'+searchwords+'%',
                    userid:this.props.user
                })
            })
                .then(response => response.json())
                .then(res => {
                    if ( res.success ){                 
                        this.props.loadMemories(res.data)  
                    }else{
                        this.props.loadMemories(null)
                    }
             })
        }else{

            fetch('https://memriio-api-0.herokuapp.com/get_memories_userid', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body:JSON.stringify({
                    userid:this.props.user
                })
            })
                .then(response => response.json())
                .then(memories=>{
                    if(memories) {
                        this.props.loadMemories(memories)
                    }else{
                        this.props.loadMemories(null)
                    }
             })
        }

    }

//---------------------------------------------------------------------------------

    render(){
        const {onRouteChange,userSignedin,currentRoute} = this.props


        if(userSignedin())

        {
            if(currentRoute === 'home')
            {
                return (
                    <nav className="dt w-100 border-box pa3 ">
                        <div className="dtc v-mid f4-ns link dim dark-gray w-25 flex justify-left" href="#" title="Home">
                            <img src={logo} className="  " alt="Site Name"></img>
                        </div>
                            <div className="dtc v-mid w-75 tr">
                                <input 
                                    onChange ={this.onSearchBoxChange}
                                    className = ' border-box w-50 ba b--black-30 pa2 br3 mb2'
                                    type="text" 
                                    aria-describedby="name-desc"></input>

                                <a className="link dim dark-gray f6 f4-ns dib mr3 mr4-ns pa2" 
                                    onClick={()=>onRouteChange("newpost")}
                                    href="#" 
                                    title="Post">Post</a>
                                <a  onClick={()=>onRouteChange("signout")}
                                    className="link dim dark-gray f6 f4-ns dib" 
                                    href="#" 
                                    title="Login">Sign Out</a>
                            </div>
                    </nav>
                )
            }else if(currentRoute === 'displayMemory')
                {
                    return (null)   
                }

            }else{
            return (
                <nav className="dt w-100 border-box pa3 ">
                    <div className="dtc v-mid f4-ns link dim dark-gray w-25 flex justify-left" href="#" title="Home">
                        <img src={logo} className="  " alt="Site Name"></img>
                    </div>
                    <div className="dtc v-mid w-75 tr">

                        <a  onClick={()=>onRouteChange ("signin")}
                            className="link dim dark-gray f6 f4-ns dib" 
                            href="#" 
                            title="Login">Sign In</a>

                    </div>
                </nav>
            )
        }
    }

}




export default Navigation;
