import React from 'react';
import {BennyCard,MemoryImage} from '../cardcollection/card'
import {Container,Row,Col} from 'react-bootstrap'
import Gallery from 'react-images-viewer'
import {getAllMemoryFiles, setHeroImage} from '../memriioserver'
import Masonry from 'react-masonry-component';


import './memoryeditor.css'



class MemoryEditor extends React.Component{

    constructor(props){
        super(props);
        this.state={
            memory:props.memory
        }
    }    

    state = {
        memory:null,
        content:null,
    }

//------------------------------------------------------------------------

    componentDidMount(){
        getAllMemoryFiles(this.props.memory.id,((memoryFiles)=>{this.setState({content:memoryFiles})}))
    }

//------------------------------------------------------------------------

    handleImageViewerClose(){
        console.log('image viewer on close ');

    }


//------------------------------------------------------------------------

    render(){
        let imageArray = []
        let gallery=null
        if(this.state.content){
            this.state.content.map((item,index)=>{
                console.log(item.fileurl)
                imageArray.push({ src: item.fileurl})
            })
            gallery = <Gallery 
                        imgs ={imageArray} 
                        isOpen = {true}
                        onClose = {this.handleImageViewerClose}
                        showThumbnails = {true}
                     />
        }else{
            //do something else here
        }

        if(this.props.memory && this.state.content){
            return (
                <Container>
                   <Row>
                        <Col>
                            <Masonry>
                                {
                                    this.state.content.map((item,i) => {
                                    return (

                                        <MemoryImage
                                            key={i} 
                                            fileurl={item.fileurl}
                                            //onExpand = {this.props.onExpandClick }
                                        />
                                        )
                                    })
                                }
                            </Masonry>
                        </Col>
                        <Col>
                            <BennyCard
                                key = {0}
                                memory={this.props.memory}
                            />  
                        </Col>
                        <Col>
                            <BennyCard
                                key = {0}
                                memory={this.props.memory}
                            />  
                        </Col>
                    </Row>           
                </Container>   
            )
        }else{
            return (
                <div>                
                    No Memory To Display
                </div>   
            )

        }
    }

}

//------------------------------------------------------------------------    


export default MemoryEditor;
