import React from 'react';
import {BennyCard,MemoryImage} from '../cardcollection/card'
import {Container,
        Row,
        Col,
        Form,
        Button,
        } from 'react-bootstrap'

import Gallery from 'react-images-viewer'
import {getAllMemoryFiles, setHeroImage} from '../memriioserver'
import Masonry from 'react-masonry-component';
import 'bootstrap/dist/css/bootstrap.min.css';


import './memoryeditor.css'



class MemoryEditor extends React.Component{

    constructor(props){
        super(props);
        this.state={
            memory:props.memory
        }
    }    

    state = {
        memory:null,
        content:null,
    }

//------------------------------------------------------------------------

    componentDidMount(){
        getAllMemoryFiles(this.props.memory.id,((memoryFiles)=>{this.setState({content:memoryFiles})}))
    }

//------------------------------------------------------------------------

    handleImageViewerClose(){
        console.log('image viewer on close ');

    }


//------------------------------------------------------------------------

    render(){
        let imageArray = []
        let gallery=null
        if(this.state.content){
            this.state.content.map((item,index)=>{
                console.log(item.fileurl)
                imageArray.push({ src: item.fileurl})
            })
            gallery = <Gallery 
                        imgs ={imageArray} 
                        isOpen = {true}
                        onClose = {this.handleImageViewerClose}
                        showThumbnails = {true}
                     />
        }else{
            //do something else here
        }

        if(this.props.memory && this.state.content){
            return (
                <Container fluid>
                   <Row>
                        <Col xs={6} float='center'>
                            <Masonry>
                                {
                                    this.state.content.map((item,i) => {
                                    return (

                                        <MemoryImage
                                            key={i} 
                                            fileurl={item.fileurl}
                                            //onExpand = {this.props.onExpandClick }
                                        />
                                        )
                                    })
                                }
                            </Masonry>
                        </Col>
                        <Col>
                            <Form className='float-left'>
                                <Form.Group controlId="formBasicEmail" >
                                    <Form.Label>Email address</Form.Label>
                                    <Form.Control type="email" placeholder="Enter email" />

                                </Form.Group>

                                <Form.Group controlId="formBasicPassword">
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control type="password" placeholder="Password" />
                                </Form.Group>
                                <Form.Group controlId="formBasicCheckbox">
                                    <Form.Check type="checkbox" label="Check me out" />
                                </Form.Group>
                                <Button variant="primary" type="submit">
                                    Submit
                                </Button>
                            </Form>
                        </Col>
                        <Col>
                            <BennyCard
                                key = {0}
                                memory={this.props.memory}
                            />  
                        </Col>
                    </Row>                 
                </Container>   
            )
        }else{
            return (
                <div>                
                    No Memory To Display
                </div>   
            )

        }
    }

}

//------------------------------------------------------------------------    


export default MemoryEditor;
