import React from 'react';

const Card =({id,userid,title,story,createdon,fileurl}) => {

    return (

        <div className='tc bg-near-white dib br3 pa3 ma2 grow bw2 shadow-5'>
            <img alt='memory' src={fileurl} className='w5'/>
            <div>
                <h2>{title}</h2>
                <h1>{fileurl+'.jpg'}</h1>
                <p>Userid:{userid}</p>
            </div>
        </div>
    )
}

const CardCollection = ({memories})=>{


    if(Array.isArray(memories)){
        return (
            <div>
                {
                    memories.map((memory,i) => {
                    return (
                        <Card 
                            key={i} 
                            id={memory.id}
                            userid={memory.userid}
                            title={memory.title}
                            story={memory.story}
                            createdon={memory.createdon}
                            fileurl={memory.fileurl}
                        />
                        )
                    })
                }
            </div>
        )
    }else{
        return (
            <div> No memories Found

            </div>
        )
    }

}


export {Card,CardCollection};
