import React from 'react';
import logo from "./memriio_logo.png"
import * as DropSearch from '../dropsearch/dropsearch'
import * as mem from '../memriioserver'

import './navigation.css'



class Navigation extends React.Component{

    constructor(props){
        super(props);
        this.state ={
            searchwords:'',
            userClouds:null,
            selectedclouds:null
        }
    }

    state={
        searchwords:'',
        userClouds:null,
        selectedclouds:null
    }

//---------------------------------------------------------------------------------    

    onSearchBoxChange = (event) =>{
        console.log('navigation-onSearchBoxChange : ' + event.target.value)
        let tmparray = []

        if(event.target.value !== ''){
            tmparray = event.target.value.split(" ")
        }


        this.searchMemories(tmparray)
        this.setState({searchwords:tmparray})        
    }

//---------------------------------------------------------------------------------

handleChangeSearchClouds = (clouds,userid) =>{
    console.log('changesearchclouds : ' + mem.stry(clouds.map(cloud => {return cloud.id})));
    this.state.selectedclouds = clouds 
    this.searchMemories(this.state.searchwords)

}

//---------------------------------------------------------------------------------

searchMemories = (searchwords) => {

console.log('navigation-searchMemories ' + searchwords.length)

    if(this.state.selectedclouds){

        if(searchwords.length>0){
            fetch('https://memriio-api-0.herokuapp.com/get_memories_userid_keywords_cloudids', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body:JSON.stringify({
                    words:searchwords,
                    userid:this.props.user,
                    cloudids:this.state.selectedclouds.map(cloud => {return cloud.value})
                })
            })
            .then(response => response.json())
            .then(res => {
                if ( res.success ){
                    this.props.loadMemories(res.data)  
                }else{
                    this.props.loadMemories(null)
                }
            })

        }else{
            fetch('https://memriio-api-0.herokuapp.com/get_memories_userid_cloudids', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body:JSON.stringify({
                    userid:this.props.user,
                    cloudids:this.state.selectedclouds.map(cloud => {return cloud.value})
                })
            })
            .then(response => response.json())
            .then(res => {
                if ( res.success ){
                    this.props.loadMemories(res.data)  
                }else{
                    this.props.loadMemories(null)
                }
            })
        }
    }else{
        this.props.loadMemories(null)
    }
}

//---------------------------------------------------------------------------------

render(){
    const {userSignedin,currentRoute} = this.props

    if(userSignedin()){
        if      (currentRoute === 'home')           { return ( this.renderStandardNav())
        }else if(currentRoute === 'displayMemory')  { return ( null ) 
        }else if(currentRoute === 'admin')          { return ( this.renderAdminNav()) }

    }else{ return ( this.renderSignInNav())}
}

//---------------------------------------------------------------------------------

loadUserClouds = (userid) => {    
    if(userid) mem.getUserClouds(userid,(clouds => {
        console.log('call back ' + mem.stry(clouds));
        this.setState({userClouds:clouds})
    }))
}

//---------------------------------------------------------------------------------

renderStandardNav =() =>{
    const {onRouteChange,user} = this.props
    let cloudbox = null
    if(this.state.userClouds){        
        cloudbox = DropSearch.cloudDropSearch( this.state.userClouds,null,true,false , this.handleChangeSearchClouds,user)
    }else{
        this.loadUserClouds(user)
    }

    return(
        <nav className='navBar'>
            <ul>
                <li className='floatRight'>
                    <a  onClick={()=>onRouteChange("signout")}
                        className="link dim dark-gray f4-ns dib mr3 mr4-ns pa2" 
                        href="#" 
                        title="Log Out">Sign Out</a>
                </li>

                <li className='floatRight'>
                    <a className="link dim dark-gray f4-ns dib mr3 mr4-ns pa2" 
                        onClick={()=>onRouteChange("admin")}
                        href="#admin" 
                        title="Administration">Admin</a>
                </li>

                <li className='floatRight รง'>
                    <input 
                        onChange ={this.onSearchBoxChange}
                        className = 'border-box mt2 ba f4-ns b--black-30 pa2 br3 mb2'
                        type="text" 
                        aria-describedby="name-desc"></input>
                </li>

                <li className='floatRight width50'>
                    {cloudbox}
                </li>                

                <li className='floatLeft'>
                    <img src={logo} className="logo" alt="memriio"></img>
                </li>
            </ul>
        </nav>
    )
}

//---------------------------------------------------------------------------------

renderSignInNav = () => {
    const onRouteChange = this.props.onRouteChange
    return (
        <nav className="navBar ">
            <div className="logocell" href="#" title="Home">
                <img src={logo} className="logo" alt="memriio"></img>
            </div>
            <div className="dtc v-mid w-75 tr">

                <a  onClick={()=>onRouteChange ("signin")}
                    className="link dim dark-gray f6 f4-ns dib" 
                    href="#" 
                    title="Log ">Sign In</a>

            </div>
        </nav>
    )
}

//---------------------------------------------------------------------------------

renderAdminNav = () => {
    const onRouteChange = this.props.onRouteChange
    return (
        <nav className="navBar ">
            <div className="logocell" href="#" title="Home">
                <img src={logo} className="logo" alt="memriio"></img>
            </div>
            <div className="dtc v-mid w-75 tr">

                <a  onClick={()=>onRouteChange ('home')}
                    className="link dim dark-gray f6 f4-ns dib" 
                    href="#" 
                    title="Home ">Home</a>

            </div>
        </nav>
    )
}

//---------------------------------------------------------------------------------

}        



export default Navigation;
