import React from 'react';
import {BennyCard,MemoryImage} from '../cardcollection/card'
import {Container,
        Row,
        Col,
        Form,
        Button,
        } from 'react-bootstrap'

import Gallery from 'react-images-viewer'
import {getAllMemoryFiles, setHeroImage} from '../memriioserver'
import Masonry from 'react-masonry-component';
import 'bootstrap/dist/css/bootstrap.min.css';


import './memoryeditor.css'



class MemoryEditor extends React.Component{

    constructor(props){
        super(props);
        this.state={
            memory:props.memory
        }
    }    

    state = {
        memory:null,
        content:null,
    }

//------------------------------------------------------------------------

    componentDidMount(){
        getAllMemoryFiles(this.props.memory.id,((memoryFiles)=>{this.setState({content:memoryFiles})}))
    }

//------------------------------------------------------------------------

    handleImageViewerClose(){
        console.log('image viewer on close ');

    }


//------------------------------------------------------------------------

    render(){
        let imageArray = []
        let gallery=null
        if(this.state.content){
            this.state.content.map((item,index)=>{
                console.log(item.fileurl)
                imageArray.push({ src: item.fileurl})
            })
            gallery = <Gallery 
                        imgs ={imageArray} 
                        isOpen = {true}
                        onClose = {this.handleImageViewerClose}
                        showThumbnails = {true}
                     />
        }else{
            //do something else here
        }

        if(this.props.memory && this.state.content){
            return (
                <Container fluid>
                   <Row>
                        <Col xs={5} float='center'>
                            <Masonry className='ba pa1 br3 h-100'>
                                {
                                    this.state.content.map((item,i) => {
                                    return (

                                        <MemoryImage
                                            key={i} 
                                            fileurl={item.fileurl}
                                            //onExpand = {this.props.onExpandClick }
                                        />
                                        )
                                    })
                                }
                            </Masonry>
                        </Col>
                        <Col>
                            <div class ='ba pa1 br3'>
                            <Form > 
                                <Form.Label className='float-left W-100' >Title</Form.Label>
                                <Form.Control type="text" value={this.props.memory.title} />

                                <Form.Label className='float-left W-100 pt2'>Description</Form.Label>
                                <Form.Control type="textarea" rows='2' value={this.props.memory.story} />

                                <Form.Label className='float-left W-100 pt2'>Location</Form.Label>
                                <Form.Control type="text" value={this.props.memory.location} />

                                <Form.Label className='float-left W-100 pt2'>Tagged People</Form.Label>
                                <Form.Control type="text" value={this.props.memory.story} />

                                <Form.Label className='float-left W-100 pt2'>Memory Clouds</Form.Label>
                                <Form.Control type="text" value={this.props.memory.location} />

                                <Form.Label className='float-left W-100 pt2'>Story</Form.Label>
                                <Form.Control as="textarea" rows='10' value={this.props.memory.story} />
                            </Form>
                            </div>
                        </Col>
                        <Col>
                            <BennyCard
                                key = {0}
                                memory={this.props.memory}
                            />  
                        </Col>
                    </Row>                 
                </Container>   
            )
        }else{
            return (
                <div>                
                    No Memory To Display
                </div>   
            )

        }
    }

}

//------------------------------------------------------------------------    


export default MemoryEditor;
