import React from 'react';
import BennyCard from '../cardcollection/card'
import {Container,Row,Col} from 'react-bootstrap'
import Gallery from 'react-images-viewer'
import {getAllMemoryFiles, setHeroImage} from '../memriioserver'


import './memoryeditor.css'



class MemoryEditor extends React.Component{

    constructor(props){
        super(props);
        this.state={
            memory:props.memory
        }
    }    

    state = {
        memory:null,
        content:[],
    }

//------------------------------------------------------------------------

    componentDidMount(){
        getAllMemoryFiles(this.props.memory.id,((memoryFiles)=>{

            this.setState({content:memoryFiles})
            console.log('inside callback : memoryFiles ' + JSON.stringify(this.state.content))
        }))
    }

//------------------------------------------------------------------------

    render(){
        const imageArray=[]
        this.state.content.map((item,index)=>{
            imageArray.push(item.fileurl)
        })
        if(this.props.memory){
            return (
                <Container>
                    <Row>
                        <Col>
                            <Gallery imgs ={imageArray} />
                        </Col>
                        <Col>

                        </Col>
                        <Col>
                            <BennyCard
                                key = {0}
                                memory={this.props.memory}
                            />  
                        </Col>

                    </Row>                   
                </Container>   
            )
        }else{
            return (
                <div>                
                    No Memory To Display
                </div>   
            )

        }
    }

}

//------------------------------------------------------------------------    


export default MemoryEditor;
