import React from 'react';

const backdropStyle ={
  position : 'fixed',
  top : 0,
  bottom : 0,
  left : 0,
  right : 0,
  backgroundColor : 'black',
  zindex:99,
  opacity: 0.8,
  padding: 50,
}

const modalStyle = {
  backgroundColor: 'white',
  boarderRadius: 5,
  maxWidth:500,
  minHeight: 300,
  margin:'0 auto',
  padding: 30,
  position: 'center',
  zindex:100,
  opacity:1,

}
const footerStyle = {
  position : 'absolule',
  bottom : 20
}


class MemoryModal extends React.Component{

    constructor(props){
        super(props);
        this.state ={
            memory:null
        }
    }

    onHide = () =>{
      this.props.onHideModal(this.props.memory)
    }
//---------------------------------------------------------------------------------

    componentDidMount = () =>{

    }

//---------------------------------------------------------------------------------


    render(){
      if(this.props.show){
        return(
          <div style ={backdropStyle}>
            <div style={modalStyle}>
              {this.props.children}
            </div>
            <div style={footerStyle} >
              <button 
                onClick={this.onHide}              
              > Hide
              </button>
            </div>
          </div>
        )
      }else{
        return null
      }        
  }

};  


export default MemoryModal;
