import BennyCard from './card'
import Masonry from 'react-masonry-component';



class CardCollection extends React.Component{

    constructor(props){
        super(props)
        this.state={
            memories:props.memories,
            activeUser:props.userid
        }
    }

    state={
        memories:null,
        activeUser:null
    }

//-------------------------------------------------------------------------------

    render(){
        if(Array.isArray(memories)){
            return (
                <Masonry>
                    {
                        memories.map((memory,i) => {
                        return (

                            <BennyCard
                                key={i} 
                                memory={memory}
                                onExpand = {this.props.onExpandClick }
                            />
                            )
                        })
                    }
                </Masonry>
            )
        }else{
            return (
                <div> 
                    No memories Found
                </div>
            )
        }   
    }


}


export default CardCollection;
