import React from 'react';
import {BaseCard,ShortCard} from './card'
import MemoryModal from '../memorymodal/memorymodal'
import Masonry from 'react-masonry-component';

import './cardcollection.css'



class CardCollection extends React.Component{

    constructor(props){
        super(props)        
        this.state={
            memories:props.memories,
            activeUser:props.userid,
            layoutcount:0
        }
    }

    state={
        memories:null,
        activeUser:null,
        showMemoryModal:false,
        activeMemory:null,       
        hack:0
    }
//-------------------------------------------------------------------------------
    onExpandMemory = (memoryId) => {



    }

//-------------------------------------------------------------------------------
componentDidUpdate = () =>{


}

//-------------------------------------------------------------------------------

onShowModal = (memory,memfiles) => {

        this.state.activeMemory = memory
        this.state.memfiles = memfiles
        this.setState({showMemoryModal:!this.state.showMemoryModal})
        if(memory){
            console.log('blah blah :' + this.state.activeMemory.title);
        }     

    }

handleLayoutComplete =(items) =>{
    this.state.hack === 0 ? this.state.hack = 1 : this.state.hack = 0
}

//-------------------------------------------------------------------------------

    render(){
        if(Array.isArray(this.props.memories)){
            console.log('CarCollection-render : memory count : ' + this.props.memories.length);
            const hack = 'col-right-' + this.state.hack
            return (
            <div>
                <div id="container">
                    <div class="col-left-" id="col1"></div>
                        <div class="col" id="col2">

                            <Masonry                                
                                ref={function(c) {this.masonry = this.masonry || c.masonry;}.bind(this)} 
                                onLayoutComplete={items =>{this.handleLayoutComplete}} 
                            >                          
                                {  this.props.memories.map((memory,i) => {
                                    let ukey = 'memcard' + i                                    
                                    if(memory.cardtype == 0){
                                        return (
                                            <BaseCard
                                                key={ukey} 
                                                memory={memory}
                                                onEdit = {this.props.onEditMemory }
                                                onExpand = {this.onExpandMemory}
                                                onShowModal = {this.onShowModal}
                                                />)
                                    }else if (memory.cardtype == 1){
                                        return (
                                            <ShortCard
                                                key={ukey} 
                                                memory={memory}
                                                onEditClick = {this.props.onEditMemory }
                                                onShowModal = {this.onShowModal}
                                            />)
                                    } 
                                    })                                   
                                }                             
                            </Masonry>       

                    </div>
                    <div class={hack} id="col3"></div>
                </div>
                <MemoryModal 
                    key         = { 'memmodal' + 42}
                    memory      = { this.state.activeMemory}
                    memfiles    = { this.state.memfiles}
                    show        = { this.state.showMemoryModal}
                    onHideModal = { this.onShowModal}>
                </MemoryModal>           
            </div>        


            )
        }else{
            return (
                <div> 
                    No memories Found
                </div>
            )
        }   
    }


}


export default CardCollection;
