export function uploadFile(originalFileName, originalFileBuffer, thumbFileName, thumbFileBuffer) {
  let originalSignedRequest = '';
  let thumbSignedRequest = '';
  let originalAWSurl = '';
  let thumbAWSurl = '';
  let origFileType = getFileMime(originalFileName);
  let thumbFileType = getFileMime(thumbFileName);
  return new Promise((resolve, reject) => {
    getUploadSignedurl(originalFileName).then(result => {
      if (result.success) {
        originalSignedRequest = result.data.signedRequest;
        originalAWSurl = result.data.url;
        getUploadSignedurl(thumbFileName).then(result => {
          if (result.success) {
            thumbSignedRequest = result.data.signedRequest;
            thumbAWSurl = result.data.url;
            uploadToS3(originalFileBuffer, originalSignedRequest, origFileType).then(result => {
              uploadToS3(thumbFileBuffer, thumbSignedRequest, thumbFileType).then(result => {
                resolve({
                  success: true,
                  originalURL: originalAWSurl,
                  thumbURL: thumbAWSurl
                });
              });
            });
          } else {
            reject({
              success: false,
              data: null,
              err: result.err
            });
          }
        });
      } else {
        reject({
          success: false,
          data: null,
          err: result.err
        });
      }
    });
  });
} //-------------------------------------------------------------------------------

function uploadToS3(file, signedRequest, fileType) {
  return new Promise((resolve, reject) => {
    const xhr = new XMLHttpRequest();
    xhr.open('PUT', signedRequest);
    xhr.setRequestHeader('Content-Type', fileType);

    xhr.onreadystatechange = () => {
      if (xhr.readyState === 4) {
        if (xhr.status === 200) {
          resolve(xhr.responseURL);
        } else {
          reject(false);
        }
      }
    };

    xhr.send(file);
  });
} //-------------------------------------------------------------------------------


export function getFileMime(fileNameAndExtension) {
  let extension = fileNameAndExtension.split('.')[1];
  let mime = '';

  switch (extension) {
    case 'mov':
      mime = 'video/quicktime';
      break;

    case 'jpg':
      mime = 'image/jpeg';
      break;

    case 'jpeg':
      mime = 'image/jpeg';
      break;

    case 'mp4':
      mime = 'video/mp4';
      break;

    case 'png':
      mime = 'image/png';
      break;
  }

  return mime;
} //-------------------------------------------------------------------------------

