    return fname
}

export function stry (str)  {
    str = JSON.stringify(str)
    if (str.charAt(0) === '"') str = str.substr(1,str.length-1)    
    if (str.charAt(str.length -1) === '"') str = str.substr(0,str.length-1)
    return str
}
