import React from 'react';
import trash from '../images/trash.png'
import edit from '../images/edit.png'
import hero from '../images/heroshot.png'
import { Player } from 'video-react';
import 'video-react/dist/video-react.css'; // import css

import {getObjectSignedurl} from '../memriioserver'
import './card.css';


//-----------------------------------------------------------------------------


//-----------------------------------------------------------------------------

class BaseCard extends React.Component{

state = {
    signedurl:''
}

handleCardExpand = () =>{
    console.log('card-handleCardExpand : memory.memid ' + this.props.memory.memid);

    this.props.onExpand(this.props.memory)
}

//-----------------------------------------------------------------------------

renderFileVIew = () => {

    if (this.props.memory.fileext == 'jpg'){
        return <img alt='memory' src={this.props.memory.fileurl} className='db br3 w-100 br--top'/>
    }else if ( this.props.memory.fileext == 'mov'){
        if(this.signedurl !== ''){

            console.log('renderFileView : ' + this.state.signedurl)


            return  (
                <Player>
                    <source src="https://media.w3.org/2010/05/sintel/trailer_hd.mp4" />
                </Player>
            )
        }

    }

}

//-----------------------------------------------------------------------------

componentDidMount = () => {

    getObjectSignedurl(this.props.memory.fileurl).then( surl => {this.setState({signedurl:surl})})

}

//-----------------------------------------------------------------------------

    render(){
        const fileview = this.renderFileVIew()
        return (

            <div className='tc bg-near-white dib br5 ma3 bw3 mw5 shadow-5 pointer'>
                {fileview}
                <div>
                    <h3>{this.props.memory.title}</h3>
                    <p className="f6 lh-copy measure mt2 mid-gray">{this.props.memory.description}</p>
                    <div className="flex justify-between">
                        <a className="link dim mb2 ml2 lh-title bw1">More</a>
                        <a className="link dim mb2 mr2 lh-title bw1" onClick={this.handleCardExpand} >[..]</a>

                    </div>

                </div>
            </div>
        )
    }

}

//-----------------------------------------------------------------------------
//-----------------------------------------------------------------------------

class ShortCard extends React.Component{

    handleCardExpand = () =>{
        console.log('card-handleCardExpand : memory.memid ' + this.props.memory.memid);

        this.props.onExpand(this.props.memory)
    }

    //-----------------------------------------------------------------------------

        render(){
            return (

                <div className='ba ma3 h-80p br3 mw5 minw5 shadow-5 pointer'>
                    <img 
                        alt='memory' 
                        src={this.props.memory.fileurl} 
                        className='br3 imgcover' 
                        on
                        />
                     <p className="f6 lh-copy measure mt2 middlecenter white i">{this.props.memory.description}</p>
                     <div className="flex bottomcenter justify-between">
                        <a className="link dim mb2 ml2 lh-title bw1">More</a>
                        <a className="link dim mb2 mr2 lh-title bw1" onClick={this.handleCardExpand} >[..]</a>
                    </div>
                </div>
            )
        }

    }

//-----------------------------------------------------------------------------
//  NEW CARD CLASS
//
//-----------------------------------------------------------------------------

class MemoryImage extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            fileurl:props.fileurl,
            fileext: props.fileext,
            index:props.index,
        }
        console.log('MemoryImage constructor : key' + this.props.index);


    }
    state={
        fileurl:null,
        index:0,
    }

//-----------------------------------------------------------------------------

handleClick = () =>{
    console.log('image clicked')

}

//-----------------------------------------------------------------------------

handleMakeHero = () =>{
    console.log('MemoryCard: handleMakeHero :' + this.props.memfile.fileurl)
    this.props.setAsHero(this.props.memfile)

}

//-----------------------------------------------------------------------------

handleEditImage = () =>{
    console.log('edit image index :' + this.props.memfile.fileurl)

}

//-----------------------------------------------------------------------------

handleDeleteImage = () =>{
    console.log('delete image index :' + this.props.memfile.fileurl)
    this.props.deleteImage(this.props.memfile)

}

//-----------------------------------------------------------------------------

renderEditControls = () => {
    return(

        <div className='bottomcenter self-center: center'>
            <img 
                className = 'dib ma4 grow-large' 
                src={hero} alt="x" 
                height="30" 
                width="30"
                onClick={this.handleMakeHero}
            ></img>
            <img 
                className = 'dib ma4 grow-large' 
                src={edit} 
                alt="x" 
                height="30" 
                width="30"
                onClick={this.handleEditImage}
            ></img>
            <img  className = 'dib ma4 grow-large' 
                src={trash} 
                alt="x" 
                height="30" 
                width="30"
                onClick={this.handleDeleteImage}
            ></img>
        </div>
    )
}
//-----------------------------------------------------------------------------

    render(){
        const controls = this.renderEditControls()
        return (

            <div className='ma3 bg-black shadow-5 pointer '>
                <img alt='memory' src={this.props.memfile.fileurl} className='vh-75 mvh75'/>''
                {controls}

            </div>
        )
    }

}

//-----------------------------------------------------------------------------

export {BaseCard,MemoryImage,ShortCard};
