import React,{useState} from 'react';
import * as mem from '../memriioserver'
import './admin.css'
import { CloudLabel } from '../buttons/buttons';
import cloudImage from '../images/cloud.png'
import tag from '../images/tag.png'
import cross from '../images/x-symbol.png'
import Modal from 'react-modal'
import {Grid,Input,Select} from 'react-spreadsheet-grid'


class Admin extends React.Component{

state ={
  clouds:[],
  users:[],
  personModalIsOpen:false
}

//---------------------------------------------------------------

componentDidMount(){
  console.log('Administration.componentDidMount ' )
  mem.getAllClouds().then(clouds => {
    console.log('all clouds');    
    console.log(clouds);
    this.setState({clouds:clouds})
    this.initColumns()
  })
}

//---------------------------------------------------------------

handleCloudSearch = (e) => {
  console.log(e.target.value);

}

//---------------------------------------------------------------

handleSelectCloud = ( cloud ) => {
  console.log(cloud.name);
  let clouds = []
  clouds.push(cloud)
  mem.getCloudPeople(clouds,null).then(people => {this.setState({people:people})})

}

handleShowAUsers = () =>{
  console.log('show all users');

  mem.getAllUsers().then(people=> {this.setState({people:people})})
}
//---------------------------------------------------------------

handlePeopleSearch = (e) => {

}

//---------------------------------------------------------------

handleAddPerson = () => { 
  this.setState({personModalIsOpen:true})

}

//---------------------------------------------------------------
handleSelectPerson = (Person) => {

}

//---------------------------------------------------------------

render(){
  const cloudview       = this.renderClouds()
  const cloudpeople     = this.renderCloudPeople()
  const addPersonModel  = this.renderAddPersonModal()
    return (
      <div className='some-page-wrapper'>
        {addPersonModel}
        <div className ='headerrow'></div>
        <div className='row'>
          {cloudview}
          {cloudpeople}
        </div>
      </div>
    )
}

//---------------------------------------------------------------

afterOpenModal =() =>{

}

closeModal = () =>{
  this.setState({personModalIsOpen:false})

}

renderAddPersonModal (){

  return(
    <Modal
          isOpen          = {this.state.personModalIsOpen}
          onAfterOpen     = {this.afterOpenModal}
          onRequestClose  = {this.closeModal}
          className       = 'modal'
          overlayClassName= 'backdropStyle'
          contentLabel    = "Example Modal"
    >
      <h2 >Add Person</h2>
      <label className="db fw6 lh-copy f6" htmlFor="name">First Name</label>
        <input 
            onChange={this.onNewUserFirstNameChange}
            className="pa2 input-reset black ba bg-transparent hover-bg-black  w-100" 
            type="text" 
            name="firstname" 
            id="firstname" />

      <button onClick={this.closeModal}>close</button>

    </Modal>    
  )

}

//---------------------------------------------------------------

renderClouds = () =>{
  let clouds = []


  if(this.state.clouds){
    { 
      clouds.push(
        <CloudLabel 
          data    = { null }
          key     = {'allclouds'} 
          onClick = { this.handleShowAUsers} 
          leftImg = { cloudImage }
          label   = { 'Master Cloud' }
        />   
      )
      this.state.clouds.map(cloud =>{
        clouds.push(
          <CloudLabel 
            data    = { cloud}
            key     = {'i' + cloud.id} 
            onClick = { this.handleSelectCloud} 
            leftImg = { cloudImage}
            label   = { cloud.name}
          />   
        )
      }) 
    }


  }

  return ( 

    <div className='admin-column cloud-column '>
      <h1 className='floatLeft'>Clouds</h1>
      <input className='searchBox' onChange={this.handleCloudSearch}/>
      <div className='floatLeft'>
      {clouds}
      </div>
    </div>
  )
}

//---------------------------------------------------------------

onColumnResize = (widthValues) => {

  const newColumns = [].concat(this.state.columns)
  Object.keys(widthValues).forEach((columnId) => {
      const column = this.state.columns.find(({ id }) => id === columnId);
      column.width = widthValues[columnId]
  })
  this.setState({columns:newColumns})
}

//---------------------------------------------------------------

initColumns = () =>{
  return(
    [
      {
        title: () => 'Photo',
        value: (row, { focus }) => {
            return ( <img src={row.avatar} alt={'image'} /> )
        },
        width:6,
      }, {

        title: () => 'User Id',
        value: (row, { focus }) => {
          return ( <Input value={row.userid} focus={focus} /> )
        },
        width:6,
      }, {
        title: () => 'First Name',
        value: (row, { focus }) => {
          return ( <Input value={row.firstname} focus={focus} /> )
        },
        width:10,
      }, {
        title: () => 'Last Name',
        value: (row, { focus }) => {
          return ( <Input value={row.lastname} focus={focus} /> )
        },
        width:10,
      }, {
        title: () => 'email',
        value: (row, { focus }) => {
          return ( <Input value={row.email} focus={focus} /> )
        },
        width:10,
      }, {
        title: () => 'Username',
        value: (row, { focus }) => {
          return ( <Input value={row.email} focus={focus} /> )
        },
        width:10,
      }, {
        title: () => 'mobile',
        value: (row, { focus }) => {
          return ( <Input value={row.mobile} focus={focus} /> )
        },
        width:10,
      }, {
        title: () => 'Joined',
        value: (row, { focus }) => {
          return ( <Input value={row.joined} focus={focus} /> )
        },
        width:10,
      }
      , {
        title: () => 'Clouds',
        value: (row, { focus }) => {
          return ( <Input value={row.joined} focus={focus} /> )
        },
        width:10,
      }
    ]
  )
}
//---------------------------------------------------------------

renderCloudPeople = () =>{

  if(this.state.people){

    return ( 
      <div className='admin-column user-column'>
        <h1 className='floatLeft'>People</h1>
        <input className='searchBox' onChange={this.handlePeopleSearch}/>
        <div className='floatLeft'>
          <Grid 
            columns = {this.state.columns}
            rows={this.state.people}
            getRowKey={row => row.userid}
            isColumnsResizable
            onColumnResize = {this.onColumnResize}
          />
        </div>
      </div>
    )
  }else{
    return null
  }


}

//---------------------------------------------------------------

}
export default Admin
