import React from 'react';

import tag from '../images/tag.png'
import './dropsearch.css'
import {ImageLabel} from '../buttons/buttons'
import SelectDropDown from 'react-dropdown-select'


//-------------------------------------------------------------------

export function peopleDropSearch ( people ){
  return (
  <SelectDropDown 
      className = 'dropSearchBox'
      options  = { people} 
      onChange = { 
          person => {
            console.log(JSON.stringify(person))
            this.handleAddTaggedPerson(person)
          }
        }
      closeOnSelect = {true}

      itemRenderer = {({ item, itemIndex, props, state, methods }) => {
        return(
          <li 
            className='wordListItem'
            key={'p' + item.userid}>
            <ImageLabel leftImg = { tag } label =   { item.firstname + ' ' + item.lastname}/>
          </li>
        )}}

  />
  )
}

//-------------------------------------------------------------------
