import React,{useState} from 'react';
import * as mem from '../memriioserver'
import './admin.css'
import { CloudLabel } from '../buttons/buttons';
import cloudImage from '../images/cloud.png'
import tag from '../images/tag.png'
import cross from '../images/x-symbol.png'
import Modal from 'react-modal'
import {Grid,Input,Select} from 'react-spreadsheet-grid'
import {getShortDate} from '../memriioserver'


class Admin extends React.Component{

state ={
  clouds:[],
  users:[],
  personModalIsOpen:false,
  columns:null,
  newuser:null
}

//---------------------------------------------------------------

componentDidMount(){
  console.log('Administration.componentDidMount ' )

  mem.getAllClouds().then(clouds => {
    console.log('all clouds');    
    console.log(clouds);
    this.setState({clouds:clouds})
    this.handleShowAUsers()
  })
}

//---------------------------------------------------------------

handleCloudSearch = (e) => {
  console.log(e.target.value);

}

//---------------------------------------------------------------

handleSelectCloud = ( cloud ) => {
  console.log(cloud.name);
  let clouds = []
  clouds.push(cloud)
  mem.getCloudPeople(clouds,null).then(people => {this.setState({people:people})})

}

handleShowAUsers = () =>{
  console.log('show all users');

  mem.getAllUsers().then(people=> {this.setState({people:people})})
}
//---------------------------------------------------------------

handlePeopleSearch = ( ) => { 

}
//---------------------------------------------------------------



//---------------------------------------------------------------
handleSelectPerson = (Person) => {

}

//---------------------------------------------------------------

render(){

  const cloudpeople     = this.renderCloudPeople()
  const addPersonModel  = this.renderAddPersonModal()
    return (
      <div className='some-page-wrapper'>
        {addPersonModel}
        <div className ='headerrow'></div>

          {cloudpeople}

      </div>
    )
}

//---------------------------------------------------------------

renderCloudPeople = () =>{

  if(this.state.people){
    if(!this.state.columns) this.state.columns = this.initColumns()

    return ( 
      <div className='admin-zone '>
        <div className='floatLeft headtext'>Users</div>
        <div className = 'searchHeader'>
          <input className='textBox' placeholder='..people search' onChange={this.handlePeopleSearch}/>
          <input className='textBox' placeholder='..cloud filter' onChange ={this.handleCloudSearch}/>
          <button id='newuserbtn' className='newButton' onClick={this.handleShowAddPersonModel}>New User</button>
        </div>

        <div className='floatLeft'>
          <Grid 
            rows={this.state.people}
            columns = {this.state.columns}
            getRowKey={row => row.userid}
            isColumnsResizable
            onColumnResize = {this.onColumnResize}
          />
        </div>
      </div>
    )
  }else{
    return null
  }


}

//--------------------------ADD PERSON MODAL -----------------------------

handleShowAddPersonModel  = ()  =>  { this.setState({personModalIsOpen:true})}
handleFirstNameChange     = (e) =>  { this.state.newuser.firstname = e.target.value }
handleLastNameChange      = (e) =>  { this.state.newuser.lastname  = e.target.value }
handleEmailChange         = (e) =>  { this.state.newuser.email     = e.target.value }
handleMobileChange        = (e) =>  { this.state.newuser.mobile    = e.target.value }
handleCloseModal          = ()  =>  { this.setState({personModalIsOpen:false})}
handleAddPerson           = ()  =>  { 
  this.setState({personModalIsOpen:false})
  console.log(this.state.newuser);

}

renderAddPersonModal (){

  return(
    <Modal
          isOpen          = {this.state.personModalIsOpen}          
          onRequestClose  = {this.closeModal}
          className       = 'modal'
          overlayClassName= 'backdropStyle'
          contentLabel    = "Example Modal"
    >

      <div className='headtext' >Add Person</div>
      <div className='labelpair'>
        <label className='labeltext spacer' htmlFor="name">First Name</label>
        <input className='textBox'  placeholder='...first Name'id='firstname' onChange={this.handleFirstNameChange}/>  
      </div>

      <div className='labelpair'>
        <label className='labeltext spacer' htmlFor="name">Last Name</label>
        <input  className='textBox'  placeholder='...last Name' id='lastname' onChange={this.handleLastNameChange}/>  
      </div>

      <div className='labelpair'>
        <label className='labeltext spacer' htmlFor="name">Email</label>
        <input className='textBox'  placeholder='...email' id='email' onChange={this.handleEmailChange}/>  
      </div>

      <div className='labelpair'>
        <label className='labeltext spacer' htmlFor="name">Mobile</label>
        <input  className='textBox' placeholder='...mobile' id='mobile' onChange={this.handleMobileChange}/>  
      </div>
      <div className='spacer'></div>
      <div className='modalfooter '>
        <button className='newButton minw50 spacer' onClick={this.handleCloudSearch}> Cancel </button>
        <button className='newButton minw50 spacer' onClick={this.handleAddPerson}> Add </button>
      </div>      

    </Modal>    
  )

}

//---------------------------------------------------------------

onColumnResize = (widthValues) => {

  const newColumns = [].concat(this.state.columns)
  Object.keys(widthValues).forEach((columnId) => {
      const column = this.state.columns.find(({ id }) => id === columnId);
      column.width = widthValues[columnId]
  })
  this.setState({columns:newColumns})
}

//---------------------------------------------------------------

initColumns = () =>{
  return(
    [
      {
        id: 'photo',
        title: () => 'Photo',
        value: (row, { focus }) => {
            return ( <img src={row.avatar} alt={'image'} /> )
        },
        width:5,
      }, {
        id: 'userid',
        title: () => 'Id',
        value: (row, { focus }) => {
          return ( <Input value={row.userid} focus={focus} /> )
        },
        width:3,
      }, {
        id: 'firstname',
        title: () => 'First Name',
        value: (row, { focus }) => {
          return ( <Input value={row.firstname} focus={focus} /> )
        },
        width:10,
      }, {
        id: 'lastname',
        title: () => 'Last Name',
        value: (row, { focus }) => {
          return ( <Input value={row.lastname} focus={focus} /> )
        },
        width:10,
      }, {
        id: 'email',
        title: () => 'email',
        value: (row, { focus }) => {
          return ( <Input value={row.email} focus={focus} /> )
        },
        width:15,
      }, {
        id: 'username',
        title: () => 'Username',
        value: (row, { focus }) => {
          return ( <Input value={row.email} focus={focus} /> )
        },
        width:15,
      }, {
        id: 'mobile',
        title: () => 'mobile',
        value: (row, { focus }) => {
          return ( <Input value={row.mobile} focus={focus} /> )
        },
        width:10,
      }, {
        id: 'joined',
        title: () => 'Joined',
        value: (row, { focus }) => {
          return ( <Input value={ getShortDate(row.joined)} focus={focus} /> )
        },
        width:8,
      }
      , {
        id: 'clouds',
        title: () => 'Clouds',
        value: (row, { focus }) => {
          return ( <Input value={getShortDate(row.joined)} focus={focus} /> )
        },
        width:24,
      }
    ]
  )
}

//---------------------------------------------------------------

}
export default Admin
