import React from 'react';
import BennyCard from '../cardcollection/card'
import Gallery from 'react-images-viewer'
import MemriioServer from '../memriioserver'


import './memoryeditor.css'



class MemoryEditor extends React.Component{

    constructor(props){
        super(props);
        this.state={
            memory:props.memory
        }
    }    

    state = {
        memory:null,
        content:[],
    }

//------------------------------------------------------------------------

    componentDidMount(){
        MemriioServer.getAllMemoryFiles().then(memoryfiles => this.setState({content:memoryfiles}))
    }

//------------------------------------------------------------------------

    render(){

        if(this.props.memory){
            return (
                <Container>
                    <Row>
                        <Col></Col>
                        <Col></Col>
                        <Col>
                            <BennyCard
                                key = {0}
                                memory={this.props.memory}
                            />  
                        </Col>

                    </Row>                   
                </Container>   
            )
        }else{
            return (
                <div>                
                    No Memory To Display
                </div>   
            )

        }
    }

}

//------------------------------------------------------------------------    


export default MemoryEditor;
