import React from 'react';
import tag from '../images/tag.png'
import cloudIMG from '../images/cloud.png'

import {ImageLabel, CloudLabel} from '../buttons/buttons'
import SelectDropDown from 'react-dropdown-select'


//-------------------------------------------------------------------

export function peopleDropSearch ( people ,callBack){

  let peoplesNames = people.map((person,idx) => {
      return {value : idx, label: (person.firstname + ' ' + person.lastname)}})
  return (



  <SelectDropDown 

      placeholder   = '+ add'
      options       = { peoplesNames } 
      searchable    = { true }
      closeOnSelect = { true }
      clearOnSelect = { true }


      //------------

      onChange = { 
          items => {
            let index = items[0].value
            if(callBack) callBack(people[index])
          }
        }

      //------------

      itemRenderer = {({ item, itemIndex, props, state, methods }) => (

        <ImageLabel 

          key={'i' + itemIndex} 
          onClick={() => methods.addItem(item)} 
          leftImg = {tag}
          label = {item.label}
        />
      )}

  />
  )
}

//-------------------------------------------------------------------

export function cloudDropSearch ( clouds, selected,showmulti,keepInList,callBack,userid){
  userid = userid || 0

  let cloudnames = clouds.map((cloud,idx) => {
      return {value : cloud.id, label: cloud.name}})

  let selectedValues = []
  if (selected){
     selected.map((cloud ) =>{
      selectedValues.push({value : cloud.id, label: cloud.name})
    })
  }

  return (

  <SelectDropDown 

      placeholder         = '+ add'
      values              = { selectedValues }
      multi               = { showmulti } 
      keepSelectedInList  = { keepInList}
      options             = { cloudnames } 
      searchable          = { true }
      closeOnSelect       = { true }
      clearOnSelect       = { true }


      //------------

      onChange = { 
          items => {
            console.log('selectDrop : ' + JSON.stringify(items));
            if(callBack) callBack(items,userid)
        }
      }

      //------------

      on

      //------------

      itemRenderer = {({ item, itemIndex, props, state, methods }) => (

        <ImageLabel 

          key={'i' + itemIndex} 
          onClick={() => methods.addItem(item)} 
          leftImg = {tag}
          label = {item.label}
        />
      )}

  />
  )
}

