handleCardSelect = (cardid) =>{
    console.log('card select : ' + cardid);
    updateCardType(this.state.memid,cardid)

}

//------------------------------------------------------------------------

handleAddCloud = (cloudid) =>{
    let alreadyTagged = false
    let newArray = this.state.memoryClouds

    this.state.memoryClouds.map(cloud =>{

       if(cloud.id == cloudid){
            alreadyTagged = true
        }
    })

    if(!alreadyTagged){
        let c = this.getUserCloudDetails(cloudid)
        if(c) newArray.push(c)
        this.setState({memoryClouds:newArray})
        console.log('handleAddCloud cloud is ' + cloudid);

        addTaggedCloud(this.state.memid ,cloudid)
    }
}

