import React from 'react';
import logo from "./memriio_logo.png"
import './navigation.css'


class Navigation extends React.Component{

    constructor(props){
        super(props);
        this.state ={
            searchwords:''
        }
    }

//---------------------------------------------------------------------------------    

    onSearchBoxChange = (event) =>{
        console.log('navigation-onSearchBoxChange : ' + event.target.value)
        let tmparray = []

        if(event.target.value !== ''){
            tmparray = event.target.value.split(" ")
        }


        this.searchMemories(tmparray)
        this.setState({searchwords:tmparray})        
    }

//---------------------------------------------------------------------------------

searchMemories = (searchwords) => {

console.log('navigation-searchMemories ' + searchwords.length)

    if(searchwords.length > 0){
        fetch('https://memriio-api-0.herokuapp.com/get_memories_keywords_user', {
        method: 'post',
        headers: {'Content-Type':'application/json'},
        body:JSON.stringify({
                words:searchwords,
                userid:this.props.user
            })
        })
            .then(response => response.json())
            .then(res => {
                if ( res.success ){                 
                    this.props.loadMemories(res.data)  
                }else{
                    this.props.loadMemories(null)
                }
            })
    }else{

        fetch('https://memriio-api-0.herokuapp.com/get_memories_userid', {
        method: 'post',
        headers: {'Content-Type':'application/json'},
        body:JSON.stringify({
                userid:this.props.user
            })
        })
            .then(response => response.json())
            .then(memories=>{
                if(memories.success) {
                    this.props.loadMemories(memories.data)
                }else{
                    this.props.loadMemories(null)
                }
            })
    }

}

//---------------------------------------------------------------------------------

renderStandardNav =() =>{
    return(
        <nav className='navBar'>
            <div className="logocell" href="#" title="Home">
                <img src={logo} className="logo" alt="memriio"></img>
            </div>
                <div className="dtc v-mid w-75 tr">
                    <input 
                        onChange ={this.onSearchBoxChange}
                        className = 'border-box mt2 w-50 ba f4-ns b--black-30 pa2 br3 mb2'
                        type="text" 
                        aria-describedby="name-desc"></input>

                    <a className="link dim dark-gray f4-ns dib mr3 mr4-ns pa2" 
                        onClick={()=>onRouteChange("admin")}
                        href="#admin" 
                        title="Administration">Admin</a>

                    <a  onClick={()=>onRouteChange("signout")}
                        className="link dim dark-gray f4-ns dib" 
                        href="#" 
                        title="Log Out">Sign Out</a>
                </div>
        </nav>
    )
}

//---------------------------------------------------------------------------------

renderSignInNav = () => {
    return (
        <nav className="navBar ">
            <div className="logocell" href="#" title="Home">
                <img src={logo} className="logo" alt="memriio"></img>
            </div>
            <div className="dtc v-mid w-75 tr">

                <a  onClick={()=>onRouteChange ("signin")}
                    className="link dim dark-gray f6 f4-ns dib" 
                    href="#" 
                    title="Log ">Sign In</a>

            </div>
        </nav>
    )
}

//---------------------------------------------------------------------------------

render(){
    const {onRouteChange,userSignedin,currentRoute} = this.props

    if(userSignedin()){
        if(currentRoute === 'home'){ return ( this.renderStandardNav())

        }else if(currentRoute === 'displayMemory'){ return (null) }

    }else{ return ( this.renderSignInNav())}
}

//---------------------------------------------------------------------------------

}        



export default Navigation;
