import React from 'react';

import tag from '../images/tag.png'
import cloudIMG from '../images/cloud.png'
import { format } from "date-fns";
import * as mem from '../memriioserver'
import './personsearch.css'
import {ImageLabel} from '../buttons/buttons'

class PersonSearchModal extends React.Component{


    state ={
        selectedPerson:null,
        clouds:[],
        people:[],
        editMode:false,
    }


  onHide = () =>{
    this.state.editMode = false
    this.props.onHideModal(this.state.selectedPerson)
  }

//---------------------------------------------------------------------------------

componentDidMount = () =>{
  this.state.clouds = this.props.clouds
  mem.getCloudPeople(this.props.clouds,(peeps) =>{ this.setState({people:peeps})})
}

//---------------------------------------------------------------------------------

renderPeople = () =>{
  const people  = this.state.people.map(
    p => {
        return (
          <li 
            className='wordListItem'
            key={'t' + p.userid}><ImageLabel leftImg = {tag} label = {p.firstname + ' ' + p.lastname}/>                                                        
          </li>
        )
      })
  return people
}

//---------------------------------------------------------------------------------

render =() =>{
  console.log('person search model ' + this.props.show);

  if(this.props.show){
    const people = this.renderPeople()
    return (
      <div className= 'backdropStyle'>
        <div className='modalStyle'>
          <div className ='scroller'>
            <ul>
              {people}
            </ul>
          </div>
        </div>
      </div>
    )
  }else{
    return null
  }    
}

};  


export default PersonSearchModal;
