import React from 'react';
import Navigation from './components/navigation/navigation.js';
import CardCollection from './components/cardcollection/cardcollection.js';
import Signin from './components/signin/signin.js';
import Register from './components/register/register.js';
import NewPost from './components/newpost/newpost.js'
//import MemoryEditor from './components/memoryeditor/memoryeditor.js';

import './App.css';
import MemoryEditor from './components/memoryeditor/memoryeditor.js';



class App extends React.Component {
  constructor(){
    super();

    this.state ={
      route: 'signin',
      isSignedIn: false,
      activeMemory:null,
      user:{
        id:'',
        name:'',
        email:'',
        joined:''
      },
      searchResult:{
        memories:[]
      }
    }
  }

//---------------------------------------------------------------------------------

  userSignedin = () => {
    return this.state.isSignedIn;
  }

//---------------------------------------------------------------------------------

  loadUser = (data) => {

    this.state.user.userid = data.userid
    this.state.user.firstname = data.firstname
    this.state.user.lastname = data.lastname
    this.state.user.email = data.email
    this.state.user.joined = data.joined


    fetch('https://memriio-api-0.herokuapp.com/get_memories_userid', {
      method: 'post',
      headers: {'Content-Type':'application/json'},
      body:JSON.stringify({
          userid:data.userid
          })
        })
          .then(response => response.json())
          .then(memories=>{
              if(memories) this.loadMemories(memories)
          })
  } 

//---------------------------------------------------------------------------------

  loadMemories =(memories) => {

    console.log('App-loadMemories : memory count : ' + memories.length);


    this.setState({searchResult:{memories : memories}})

  }

//---------------------------------------------------------------------------------

  onRouteChange = (route) =>{

    console.log('Apps - onRouteChange : route ' + route);

    if(route === 'signout'){
      this.setState({route:route, isSignedIn : false})
    }else if (route === 'home'){
      this.setState({route:route, isSignedIn: true})
    }else if (route === 'memoryUpdated'){
      this.loadUser(this.state.user)
      this.setState({route:'home', isSignedIn: true})
    }else{
      this.setState({route:route})
    }

  }

//---------------------------------------------------------------------------------

displayMemory = (memory) => {


  this.state.activeMemory = memory;
  console.log('App.displayMemory : activeMemory.id ' + this.state.activeMemory.id);
  this.onRouteChange('displayMemory')

}

//---------------------------------------------------------------------------------

  render() {
    return (
      <div className="App">
        <Navigation 
          onRouteChange={this.onRouteChange} 
          userSignedin={this.userSignedin}
          loadMemories={this.loadMemories}           
          currentRoute={this.state.route}
          user={this.state.user.id}/>

        { this.state.route === 'home' 
          ? <CardCollection 
              memories={this.state.searchResult.memories} 
              userid={this.state.user.id} 
              onExpandClick={this.displayMemory}
            />
          : (this.state.route === 'signin'
            ?<Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange} />
            :(this.state.route === 'newpost')
              ?<NewPost userid={this.state.user.id} onRouteChange={this.onRouteChange} />
              :(this.state.route === 'displayMemory')
              ?<MemoryEditor 
                  memory={this.state.activeMemory}
                  onRouteChange={this.onRouteChange}
              />
              :<Register loadUser={this.loadUser} onRouteChange={this.onRouteChange} />


          )
        }

      </div>
    );
  }
};

export default App;
