import React from 'react';
import {BennyCard,MemoryImage} from '../cardcollection/card'
import {Container,
        Row,
        Col,
        Form,
        Button,
        Carousel,
        } from 'react-bootstrap'

import Gallery from 'react-images-viewer'
import {getAllMemoryFiles, setHeroImage} from '../memriioserver'
import Masonry from 'react-masonry-component';
import 'bootstrap/dist/css/bootstrap.min.css';


import './memoryeditor.css'





class MemoryEditor extends React.Component{

    constructor(props){
        super(props);
        this.getCompiledMemory = this.getCompiledMemory.bind(this)
        this.state = {
                id:props.memory.id,
                title:props.memory.title,
                description:props.memory.description,
                story:props.memory.story,
                location:props.memory.location,
                owner:props.memory.userid,

                taggedPeople:[],
                memoryClouds:[],
                searchWords:[],
                memfiles:null,

                uploadNeeded:false,
            }

    }    

    state = {

        id:0,
        owner:0,
        title:'',
        description:'',
        location:'',        
        story:'',

        taggedPeople:[],
        memoryClouds:[],
        searchWords:[],
        memfiles:null,

        uploadNeeded:false,
    }

//------------------------------------------------------------------------

    componentDidMount(){
        getAllMemoryFiles(this.props.memory.id,((memoryFiles)=>{this.setState({memfiles:memoryFiles})}))
    }

//---------------------------------------------------------------------------------    

    getCompiledMemory = () =>{


        let memory =  {
            id:this.state.id,
            owner:this.state.owner,
            title:this.state.title,
            description:this.state.description,
            location:this.state.location,
            story:this.state.story,
            fileurl:this.props.memory.fileurl

        }
        console.log('memoryeditor.getCompiledMemory : memory : ' + memory.fileurl);


    }


//------------------------------------------------------------------------

    render(){
        let imageArray = []


        if(this.state.memfiles){
            this.state.memfiles.map((item,index)=>{imageArray.push({ src: item.fileurl})})
        }else{
            //do something else here
        }

        if(this.props.memory && this.state.memfiles){
            return (
                <Container fluid>
                   <Row>
                        <Col xs={5} float='center'>
                            <Carousel className='ba pa2 br3' interval={600000}>
                                {
                                    this.state.memfiles.map((item,i) => {
                                    return (
                                        <Carousel.Item>
                                            <MemoryImage
                                                key={i} 
                                                fileurl={item.fileurl}
                                                //onExpand = {this.props.onExpandClick }
                                            />
                                        </Carousel.Item>
                                        )
                                    })
                                }
                            </Carousel>
                        </Col>
                        <Col>
                            <div class ='ba pa2 br3'>
                            <Form > 
                                <Form.Label className='float-left W-100' >Title</Form.Label>
                                <Form.Control 
                                    type="text" 
                                    value={this.state.title} 
                                    onChange={(event) => {this.setState({title:event.value,uploadNeeded:true})}}
                                />

                                <Form.Label className='float-left W-100 pt2'>Description</Form.Label>
                                <Form.Control
                                    type="text" 
                                    value={this.state.description} 
                                    onChange={(event) => {this.setState({description:event.value,uploadNeeded:true})}}
                                />

                                <Form.Label className='float-left W-100 pt2'>Location</Form.Label>
                                <Form.Control
                                    type="text" 
                                    value={this.state.location} 
                                    onChange={(event) => {this.setState({location:event.value,uploadNeeded:true})}}
                                />

                                <Form.Label className='float-left W-100 pt2'>Tagged People</Form.Label>
                                <Form.Control type="text" value='NOT IMPLEMENTED' />

                                <Form.Label className='float-left W-100 pt2'>Memory Clouds</Form.Label>
                                <Form.Control type="text" value='NOT IMPLEMENTED' />

                                <Form.Label className='float-left W-100 pt2'>Story</Form.Label>
                                <Form.Control 
                                    as="textarea" 
                                    rows='10' 
                                    value={this.state.story} 
                                    onChange={(event) => {this.setState({story:event.value,uploadNeeded:true})}}
                                />
                            </Form>
                            </div>
                        </Col>
                        <Col>
                            <div class ='ba pa2 br3'>
                                <BennyCard
                                    key = {0}
                                    memory={this.getCompiledMemory()}
                                />  
                            </div>
                        </Col>
                    </Row>                 
                </Container>   
            )
        }else{
            return (
                <div>                
                    No Memory To Display
                </div>   
            )

        }
    }

}

//------------------------------------------------------------------------    


export default MemoryEditor;
