import React,{useState} from 'react';
import ReactDOM from 'react-dom';
import {BaseCard,MemoryImage} from '../cardcollection/card'
import {SubTag} from '../buttons/buttons'
import 'bootstrap/dist/css/bootstrap.min.css';
import './memoryeditor.css'
import logo from "../navigation/Logo_black.png"
import {Icon, ICONS} from '../icons/icons'



import {getAllMemoryFiles, 
        getTaggedPeople,
        getMemoryClouds,
        getCloudPeople,
        updateHeroImage,
        updateDescription,
        updateLocation,
        updateStory,
        updateTitle,
        updateTaggedPeople,
        updateClouds,
        deleteMemory,
        addTaggedPerson,

       } from '../memriioserver'


import {Container,
        Row,
        Col,
        Form,
        Carousel,
        Button,
        Dropdown,
        DropdownButton,
        Alert,
        } from 'react-bootstrap'


class MemoryEditor extends React.Component{

    constructor(props){
        super(props);
        console.log('location ' + props.memory.title);

        this.state = {
                memid:props.memory.memid,
                title:props.memory.title,
                description:props.memory.description,
                story:props.memory.story,
                location:props.memory.location,
                owner:props.memory.memid,
                fileurl:props.memory.fileurl,

                taggedPeople:[],
                memoryClouds:[],
                searchWords:[],
                memfiles:null,
                cloudPeople:[],

                uploadNeeded:false,
                storyExpanded:false,
            }

    }    



//------------------------------------------------------------------------

    componentDidMount(){


        getAllMemoryFiles   (this.props.memory.memid,((memoryFiles)=>{this.setState({memfiles:memoryFiles})}))
        getTaggedPeople     (this.props.memory.memid,((people)=>{this.setState({taggedPeople:people})}))
        getMemoryClouds     (this.props.memory.memid,((clouds)=>{this.populateCloudInfo(clouds)}))


    }



//------------------------------------------------------------------------

titleChange         = (event) => {this.setState({title:event.target.value,uploadNeeded:true})}
descriptionChange   = (event) => {this.setState({description:event.target.value,uploadNeeded:true})}
locationChange      = (event) => {this.setState({location:event.target.value,uploadNeeded:true})}
storyChange         = (event) => {this.setState({story:event.target.value,uploadNeeded:true})}
handleExpandStory   =  ()   =>   {
    const expanded = !this.state.storyExpanded
    this.setState({storyExpanded:expanded})
    console.log('storyExpanded ' + this.state.storyExpanded);

}

//------------------------------------------------------------------------

populateCloudInfo = (clouds) =>{

    this.setState({memoryClouds:clouds})
    getCloudPeople (clouds,((people)=>{this.setState({cloudPeople:people})}))


}
//------------------------------------------------------------------------

handleDeleteMemory = () =>{
    console.log('handleDeleteMemory ' + this.state.id);
    deleteMemory(this.state.id)
    this.props.onRouteChange('memoryUpdated')
}


//------------------------------------------------------------------------

updateMemory = () =>{

    updateTitle         (this.state.memid,this.state.title)
    updateDescription   (this.state.memid,this.state.description)
    updateLocation      (this.state.memid,this.state.location)
    updateStory         (this.state.memid,this.state.story)



    this.props.onRouteChange('memoryUpdated')

}

//------------------------------------------------------------------------

addPerson = (userid,event) =>{
    let alreadyTagged = false
    let newArray = this.state.taggedPeople

    this.state.taggedPeople.map(person =>{

       if(person.userid == userid){
            alreadyTagged = true
        }
    })

    if(!alreadyTagged){
        let p = this.getCloudPersonDetails(userid)
        if(p) newArray.push(p)
        this.setState({taggedPeople:newArray})
        console.log('addPerson memid is ' + this.state.memid);

        addTaggedPerson(this.state.memid ,p.userid)
    }
}

//------------------------------------------------------------------------

getCloudPersonDetails = (userid) =>{
    let person = null
    this.state.cloudPeople.map(p =>{
        if(p.userid == userid){person = p}
    })
    return person
}

//------------------------------------------------------------------------

handleDeleteTaggedPerson = (userid) =>{

    let array = this.state.taggedPeople

    this.state.taggedPeople.map((p,index) =>{
        if(p.userid == userid || p.userid == userid){
            array.splice(index,1)
        }
    })
    this.setState({taggedPeople:array})
}

//------------------------------------------------------------------------

handleDeleteCloud = (cloudid) =>{
    alert('handleDeleteCloud : cloudid ' + cloudid)
}

//------------------------------------------------------------------------


    render(){
        let imageArray = []
        let upperform = null
        let lowerform = null


        if(this.state.memfiles){
            this.state.memfiles.map((item,index)=>{imageArray.push({ src: item.fileurl})})
        }else{
            //do something else here
        }

        if(!this.state.storyExpanded){
            upperform = 
                    <div>
                        <Form.Label className='float-left W-100' >Title</Form.Label>
                        <Form.Control 
                            type="text" 
                            value={this.state.title} 
                            onChange={this.titleChange}
                        />

                        <Form.Label className='float-left W-100 pt2 pb0'>Description</Form.Label>
                        <Form.Control
                            type="text" 
                            value={this.state.description} 
                            onChange={this.descriptionChange}

                        />

                        <Form.Label className='float-left W-100 pt2 pb0'>Location</Form.Label>
                        <Form.Control
                            type="text" 
                            value={this.state.location} 
                            onChange={this.locationChange}
                        />

                        <Form.Label className='float-left W-100 pt2'>Tagged People</Form.Label>
                            <div className='dib  w-100 float-left'>
                                {this.state.taggedPeople.map(person => {
                                    let fullname = person.firstname + ' ' + person.lastname

                                        return(<SubTag 
                                                tagKey={person.userid}
                                                text={fullname} 
                                                onClick={() => this.handleDeleteTaggedPerson(person.userid)}
                                              />)
                                    })
                                }
                                <div className='inline-flex ma1 br3' >
                                    <DropdownButton
                                        id='dropdown-basic-button'
                                        size="sm"
                                        title="++"
                                        >
                                        {this.state.cloudPeople.map((person,index) => {

                                            return(
                                                <Dropdown.Item 
                                                    eventKey={person.userid.toString()}
                                                    onSelect={this.addPerson}
                                                >
                                                    {person.firstname + ' ' + person.lastname}
                                                </Dropdown.Item>)})
                                        } 
                                    </DropdownButton> 
                                 </div>    
                            </div>

                        <Form.Label className='float-left W-100 pt2'>Memory Clouds</Form.Label>
                            <div className='dib  w-100 float-left'>
                                {this.state.memoryClouds.map(cloud => {

                                        return(<SubTag 
                                                tagKey={cloud.id}
                                                text={cloud.name} 
                                                onClick={() => this.handleDeleteCloud(cloud.id)}
                                            />)
                                    })
                                }
                            </div>
                    </div>
            lowerform = 
                    <div>
                        <div className='inline-flex w-100 justify-between'>
                            <Form.Label className=' W-50 pt2'>Story</Form.Label>
                            <Icon icon={ICONS.CHEVRON_UP} onClick={this.handleExpandStory}/>
                        </div>

                        <Form.Control 
                            as="textarea" 
                            rows='10' 
                            value={this.state.story} 
                            onChange={this.storyChange}
                        />
                    </div>

        }else{
            upperform = null
            lowerform = 
                    <div>
                        <div className='inline-flex w-100 justify-between'>
                            <Form.Label className=' W-50 pt2'>Story</Form.Label>
                            <Icon icon={ICONS.CHEVRON_DOWN} onClick={this.handleExpandStory}/>
                        </div>

                        <Form.Control 
                            as="textarea" 
                            rows='10' 
                            value={this.state.story} 
                            onChange={this.storyChange}
                        />
                    </div>
        }

        if(this.props.memory && this.state.memfiles){
            return (
                <div>

                    <nav className="dt w-100 border-box pa3 ">
                            <a className="dtc v-mid f4-ns link dim dark-gray w-25 flex justify-left" href="#" title="Home">
                            <img src={logo} className="w2 h2 " alt="Site Name"></img>
                                memriio</a>
                                <div className="dtc v-mid w-75 tr">

                                    <a className="link dim dark-gray f6 f4-ns dib mr3 mr4-ns pa2" 
                                        onClick={()=>this.props.onRouteChange("home")}
                                        href="#" 
                                        title="Post">Cancel</a>
                                    <a className="link dim dark-gray f6 f4-ns dib mr3 mr4-ns pa2" 
                                        onClick={this.updateMemory}
                                        href="#" 
                                        title="Post">Update</a>
                                    <a  onClick={this.handleDeleteMemory}
                                        className="link dim dark-gray f6 f4-ns dib" 
                                        href="#" 
                                        title="Login">Delete</a>
                                </div>
                        </nav>


                <Container fluid>
                   <Row>
                        <Col xs={5} float='center'>
                            <Carousel 
                                className='ba pa2 br3' 
                                autoPlay={false} 
                                interval={1200000}
                                >
                                {
                                    this.state.memfiles.map((item,i) => {
                                    return (
                                        <Carousel.Item>
                                            <MemoryImage
                                                key={i} 
                                                index={i}
                                                fileurl={item.fileurl}
                                            />
                                        </Carousel.Item>
                                        )
                                    })
                                }

                            </Carousel>
                        </Col>
                        <Col>
                            <div class ='ba pa2 br3'>
                            <Form > 
                               {upperform}
                               {lowerform}

                            </Form>
                            </div>
                        </Col>
                        <Col>
                            <Carousel 
                                className='ba pa2 br3 ' 
                                interval={1200000}
                                nextIcon = {<Icon icon={ICONS.CHEVRON_RIGHT}/>}
                                prevIcon = {<Icon icon={ICONS.CHEVRON_LEFT}/>}
                            >
                                {

                                    this.state.memfiles.map((item,i) => {
                                    return (
                                        <Carousel.Item>
                                            <BaseCard
                                                key = {i}
                                                memory={this.state}
                                            /> 
                                        </Carousel.Item>

                                        )
                                    })

                                }

                            </Carousel>

                        </Col>
                    </Row>                 
                </Container>   
            </div>  
            )
        }else{
            return (
                <div>                
                    No Memory To Display

                </div>   
            )

        }
    }

}

