import React from 'react';
import './card.css';


//-----------------------------------------------------------------------------

class BennyCard extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            memory:props.memory,
            onExpand:props.onExpand,
        }
    }
    state={
        memory:null,
        onExpand:null
    }

//-----------------------------------------------------------------------------

handleCardExpand = () =>{

    this.state.onExpand(this.state.memory)
}


//-----------------------------------------------------------------------------

    render(){
        return (

            <div className='tc bg-near-white dib br5 ma3 bw3 mw5  shadow-5 pointer'>
                <img alt='memory' src={this.state.remoteurls[0]} className='db br3 w-100 br--top'/>
                <div>
                    <h3>{this.state.title}</h3>
                    <p className="f6 lh-copy measure mt2 mid-gray">{this.state.description}</p>
                    <div className="flex justify-between">
                        <a className="link dim mb2 ml2 lh-title bw1">More</a>
                    <a className="link dim mb2 mr2 lh-title bw1" onClick={this.handleCardExpand} >[..]</a>

                    </div>

                </div>
            </div>
        )
    }

}

//-----------------------------------------------------------------------------

export default BennyCard;
