import React from 'react';

import tag from '../images/tag.png'
import './dropsearch.css'
import {ImageLabel} from '../buttons/buttons'
import SelectDropDown from 'react-dropdown-select'


//-------------------------------------------------------------------

export function peopleDropSearch ( people ,callBack){

  let peoplesNames = people.map((person,idx) => {
      return {value : idx, label: (person.firstname + ' ' + person.lastname)}})
  return (



  <SelectDropDown 
      placeholder   = '+ add'
      options       = { peoplesNames } 
      searchable    = { true }
      closeOnSelect = { true }
      clearOnSelect = { true }

      //------------

      onChange = { 
          items => {
            let index = items[0].value
            console.log(people[index]);
            if(callBack) callBack(people[index])
          }
        }

      //------------

      itemRenderer = {({ item, itemIndex, props, state, methods }) => {

        <div onClick={() => methods.addItem(item)}>
          <ImageLabel leftImg = {tag} label = {item.label}/>
        </div>
      }}

  />
  )
}

//-------------------------------------------------------------------
