import React from 'react';

import tag from '../images/tag.png'
import './dropsearch.css'
import {ImageLabel} from '../buttons/buttons'
import SelectDropDown from 'react-dropdown-select'


//-------------------------------------------------------------------

export function peopleDropSearch ( people ){

  let peoplesNames = people.map((person,idx) => {
      return {value : idx, label: (person.firstname + ' ' + person.lastname)}})
  return (

  <SelectDropDown 
      className = 'dropSearchBox' 
      options  = { peoplesNames } 

      onChange = { 
          items => {
            let index = items[0].value
            console.log(people[index]);

          //this.handleAddTaggedPerson(person)
          }
        }
      closeOnSelect = {true}
      clearOnSelect = {true}
      multi = {false}



  />
  )
}

