import React from 'react';
import {BaseCard,ShortCard} from './card'
import MemoryModal from '../memorymodal/memorymodal'
import Masonry from 'react-masonry-component';

import './cardcollection.css'



class CardCollection extends React.Component{

    constructor(props){
        super(props)
        console.log('CardCollection constructor : mem count : ' + props.memories.length);

        this.state={
            memories:props.memories,
            activeUser:props.userid
        }
    }

    state={
        memories:null,
        activeUser:null,
        showMemoryModal:false,
        currentMemory:null
    }
//-------------------------------------------------------------------------------
    onExpandMemory = (memoryId) => {
        console.log('CardCollection->onExpandMemory memory id : ' + memoryId)
        this.masonry.layout()
    }

//-------------------------------------------------------------------------------

onShowModal = (memory) => {
        console.log('CardCollection->onExpandMemory memory id : ' + memory.memid)
        this.setState({showMemoryModal:!this.state.showMemoryModal,currentMemory:memory})
    }

//-------------------------------------------------------------------------------

    render(){
        if(Array.isArray(this.props.memories)){
            console.log('CarCollection-render : memory count : ' + this.props.memories.length);

            return (
                <div id="container">
                    <MemoryModal 
                        show ={this.state.showMemoryModal}
                        onHideModal = {this.onShowModal}
                    >
                        Hello Modal
                    </MemoryModal>

                    <div class="col-left" id="col1"></div>
                    <div class="col" id="col2">
                        <Masonry
                            ref={function(c) {this.masonry = this.masonry || c.masonry;}.bind(this)}
                        >
                            {
                                this.props.memories.map((memory,i) => {
                                    let ukey = 'memcard' + i                                    
                                    if(memory.cardtype == 0){
                                        return (
                                            <BaseCard
                                                key={ukey} 
                                                memory={memory}
                                                onEdit = {this.props.onEditMemory }
                                                onExpand = {this.onExpandMemory}
                                                onShowModal = {this.onShowModal}
                                                />)
                                    }else if (memory.cardtype == 1){
                                        return (
                                            <ShortCard
                                                key={ukey} 
                                                memory={memory}
                                                onEditClick = {this.props.onEditMemory }/>)  

                                    }

                                })
                            }
                        </Masonry>

                    </div>
                    <div class="col-right" id="col3"></div>
                </div>



            )
        }else{
            return (
                <div> 
                    No memories Found
                </div>
            )
        }   
    }


}


export default CardCollection;
