import React from 'react';
import Xsymbol from '../images/x-symbol.png'
import './button.css'

export class SubTag extends React.Component{

    render(){
        const{text,tagKey} = this.props
        return(

            <div className='float-left inline-flex ba ma1 pa1 br3 b--blue bg-near-white '>
                <span className='dib f6'>{text}</span>                
                <img 
                    className = 'dib ma1 pointer' 
                    src={Xsymbol} 
                    alt="X" 
                    height="15" 
                    width="15"

                    onClick={this.props.onClick}
                ></img>


            </div>
        )
    }
}

//------------------------------------------------------------------------------------------
//------------------------------------------------------------------------------------------

export class CheckBox extends React.Component{

    state ={checked:false}

    //--------------------------------------------

    onClick = () =>{
        this.setState({checked:!this.state.checked})
        this.props.onClick
    }

    //--------------------------------------------

    render(){
        const{checkedImg,label} = this.props

        if(this.state.checked){
            source = checkedImg
        }else{
            source = null
        }

        return(
            <div    className='checkbox di pointer'
                    onClick={this.onClick}   
            >
                <img 
                    src={source}                     
                    height="15" 
                    width="15"  
                ></img>
                <label>{label}</label>

            </div>
        )
    }

}
