import React from 'react';
import trash from '../images/trash.png'
import edit from '../images/edit.png'
import hero from '../images/heroshot.png'
import {getObjectSignedurl,getAllMemoryFiles} from '../memriioserver'
import ReactPlayer from 'react-player';
import './card.css';


//-----------------------------------------------------------------------------


class BaseCard extends React.Component{

state = {
    memfiles:null,
    viewState:0
}

//------------------------------------------------------------------------------

handleCardExpand = () =>{
    console.log('card-handleCardExpand : memory.memid ' + this.props.memory.memid);

    this.props.onExpand(this.props.memory)
}

//-----------------------------------------------------------------------------


renderFileVIew = () => {

    if(this.state.memfiles){
        let memf = this.getHeroFile()
        return <img alt='memory' src={memf.thumburl} className='bigcardimg'/>


    }
}


//-----------------------------------------------------------------------------

handleMoreClick =() =>{
    if(this.state.viewState === 0) this.setState({viewState:1})
    else if(this.state.viewState === 1) this.setState({viewState:0})
    console.log('viewState : ' + this.state.viewState);

}

//-----------------------------------------------------------------------------

