    state = {
        memfiles:null
    }
    //-----------------------------------------------------------------------------


renderFileVIew = () => {

    if(this.state.memfiles) {
        let memf = this.getHeroFile()
        return <img alt='memory' src={memf.thumburl} className='br3 imgcover'/>
    }

}
