import React from 'react';
import './memorymodal.css'

import {getAllMemoryFiles, 
  getTaggedPeople,
  getMemoryClouds,
  getMemorySearchWords, 
  getCloudPeople,
 } from '../memriioserver'
import {SubTag} from '../buttons/buttons'


class MemoryModal extends React.Component{


    state ={
        memory:null,
        taggedPeople:[],
        memoryClouds:[],
        searchWords:[], 
        keywords:[],    // this is temporary   
        memfiles:null,
        cloudPeople:[],
        userClouds:[],
        activefile:null
    }


    onHide = () =>{
      this.props.onHideModal(this.props.memory)
    }
//---------------------------------------------------------------------------------

  componentDidUpdate = (prevProps,prevState) =>{

    if(this.props.memory)
    {
      if(!prevProps.memory || this.props.memory.memid !== prevProps.memory.memid){

        this.populateMemoryFiles(this.props.memfiles)
        getTaggedPeople      (this.props.memory.memid,((people)=>{this.setState({taggedPeople:people})}))
        getMemoryClouds      (this.props.memory.memid,((clouds)=>{this.populateCloudInfo(clouds)}))
        getMemorySearchWords (this.props.memory.memid,((words =>{this.populateSearchWords(words)})))

      }      
    }
  }

//---------------------------------------------------------------------------------

  getHeroFile = () => {
    let hero = this.state.memfiles[0]    
    this.state.memfiles.map(memfile => {
        if(memfile.ishero){           
            hero = memfile
        }
    })
    return hero
  }
//---------------------------------------------------------------------------------
  populateMemoryFiles = (memfiles) => {

    this.state.memfiles = memfiles
    this.setState({
        activefile:this.getHeroFile()
    })
  }
//---------------------------------------------------------------------------------
  populateSearchWords = (words) =>{
    this.state.keywords = []
    words.map(worditem =>{
        this.state.keywords.push(worditem.keyword)
    })
    this.setState({searchWords:words})
  }

//------------------------------------------------------------------------

  populateCloudInfo = (clouds) =>{

    this.setState({memoryClouds:clouds})
    getCloudPeople (clouds,((people)=>{this.setState({cloudPeople:people})}))


  }

  //------------------------------------------------------------------------

  renderMemoryContent = () => {
    const mem = this.props.memory
    const keywords    = this.state.keywords.map((word, idx) => {return <li key={'k'+idx}>{word}</li>})
    const people      = this.state.taggedPeople.map((person, idx) => {return <li key={person.userid}>{person.firstname+' '+person.lastname}</li>})

    if(this.state.activefile){
      return (
          <div className='modalrow'>

            <div className='imgcolumn'>
              <img 
                alt='memory' 
                src={this.state.activefile.thumburl} 
                className = 'imgStyle'
              />
            </div>

            <div className = 'infoColumn'>

              <h4>{mem.title} </h4>
              <p className="f6 lh-copy i measure mt2 mid-gray">{mem.description}</p>
              <p className="f6 lh-copy measure mt2 black"> { mem.story } </p>

            </div>  

            <div className = 'detailsColumn'>

              <h4>Key words </h4>
              <ul>{keywords} </ul>
              <h4>Key People</h4>
              <ul>{people} </ul>
              <div className='footerStyle'>
                <SubTag className='closeBtn' onClick={this.onHide} />
              </div>


            </div>  

          </div>
      )
    } else {
      return null
    }

  }

//------------------------------------------------------------------------

    render(){


      if(this.props.show){
        let memoryContent = this.renderMemoryContent()
        return(
          <div className= 'backdropStyle'>
            <div className='modalStyle'>
              {memoryContent}              
            </div>
          </div>
        )
      }else{
        return null
      }        
  }

};  


export default MemoryModal;
