import React from 'react';
import { BennyCard} from './card'
import './grid.css'

$('.grid').isotope({
    layoutMode: 'fitColumns',
    itemSelector: '.grid-item'
  });


function BasicCard ({id,userid,title,story,createdon,fileurl})  {

    return (

        <div className='tc bg-near-white dib br5 ma2 bw3 mw5 grow shadow-5'>
            <img alt='memory' src={fileurl} className='db br3 w-100 br--top'/>
            <div>
                <h3>{title}</h3>
                <p class="f6 lh-copy measure mt2 mid-gray">{story}</p>
                <a href="#" class="link dim lh-title bw1">Learn more</a>


            </div>
        </div>

    )
}  

const CardCollection = ({memories})=>{


    return (


        <div class="grid" >
            <div class="grid-item grid-item--height2"></div>
            <div class="grid-item"></div>
            <div class="grid-item"></div>
            <div class="grid-item"></div>
            <div class="grid-item grid-item--height2"></div>
            <div class="grid-item grid-item--height2"></div>
            <div class="grid-item"></div>
            <div class="grid-item"></div>
            <div class="grid-item grid-item--height2"></div>
            <div class="grid-item"></div>
            <div class="grid-item grid-item--height2"></div>
            <div class="grid-item"></div>
            <div class="grid-item"></div>
            <div class="grid-item"></div>
        </div>
    )


}


export {CardCollection}; 







