import React from 'react';
import tick from '../images/checkmark.png'
import tag from '../images/tag.png'
import author from '../images/author.png'
import x from '../images/x-symbol.png'
import edit from '../images/edit.png'
import left from '../images/chevron-left.png'
import right from '../images/chevron-right.png'
import trash from '../images/trash.png'
import hero from '../images/hero-full.png'
import heroOutline from '../images/hero-outline-grey.png'
import MemoryFileViewer from '../memoryviewer/memoryImageViewer'
import * as Memriio from '../memriioserver'
import WordExctractor from 'keyword-extractor'
import * as Utils from './utils'
import './memorymodal.css'

import {getAllMemoryFiles, 
  getTaggedPeople,
  getMemoryClouds,
  getMemorySearchWords, 
  getCloudPeople,
  getUser,
 } from '../memriioserver'
import {CheckBox,SubTag,ImageLabel} from '../buttons/buttons'



class MemoryModal extends React.Component{


    state ={
        memory:null,
        taggedPeople:[],
        memoryClouds:[],
        searchWords:[],         
        memfiles:null,
        cloudPeople:[],
        userClouds:[],
        activefile:null,
        author:null,
        memfileIndex:0,
        editMode:false,
    }


  onHide = () =>{
    this.state.editMode = false
    this.props.onHideModal(this.props.memory)
  }
//---------------------------------------------------------------------------------

  componentDidUpdate = (prevProps,prevState) =>{

    if(this.props.memory)
    {
      if(!prevProps.memory || this.props.memory.memid !== prevProps.memory.memid){

        this.populateMemoryFiles(this.props.memfiles)
        getUser              (this.props.memory.userid,(author => {
                                                              this.setState({author:author})
                                                              console.log('Author + ' + JSON.stringify(author))
                                                          }))
        getTaggedPeople      (this.props.memory.memid,((people)=>{this.setState({taggedPeople:people})}))
        getMemoryClouds      (this.props.memory.memid,((clouds)=>{this.populateCloudInfo(clouds)}))
        getMemorySearchWords (this.props.memory.memid,((words =>{this.populateSearchWords(words)})))



      }      
    }
  }

//---------------------------------------------------------------------------------

  getHeroFile = () => {
    this.state.memfileIndex=0
    let hero = this.state.memfiles[0]

    this.state.memfiles.map((memfile,index)=> {

        if(memfile.ishero){         
            this.state.memfileIndex = index            
            hero = memfile
        }
    })
    return hero
  }
//---------------------------------------------------------------------------------
  populateMemoryFiles = (memfiles) => {

    this.state.memfiles = memfiles
    this.setState({
        activefile:this.getHeroFile()
    })
  }
//---------------------------------------------------------------------------------
  populateSearchWords = (words) =>{
    this.state.keywords = []
    words.map(worditem =>{
        this.state.keywords.push(worditem.keyword)
    })
    this.setState({searchWords:words})
  }

//------------------------------------------------------------------------

  populateCloudInfo = (clouds) =>{
    this.setState({memoryClouds:clouds})
    getCloudPeople (clouds,((people)=>{this.setState({cloudPeople:people})}))   
  }

//------------------------------------------------------------------------

  toggleEditMode =() =>{
    this.setState({editMode:!this.state.editMode})

  }

  //------------------------------------------------------------------------

  userIsAuthorisedToEdit = () =>{
    return true
  }

  //------------------------------------------------------------------------

  deleteFileFromMemory =() =>{
    console.log('delte file from memroy');

  }

  //------------------------------------------------------------------------

  makeHeroFile = () =>{
    console.log('make hero file');

  }

  //------------------------------------------------------------------------

  activeFileisHero =() =>{
    var afile = this.state.activefile

    if(afile){
      if(afile.ishero){
        return true
      }else{
        return false
      }
    }else{
      return false
    }
  }

  //------------------------------------------------------------------------

  render(){

    if(this.props.show){
      let memoryContent = this.renderMemoryContent()
      let carouselControls = this.renderCarouselControls()
      return(
        <div className= 'backdropStyle'>
          <div className='modalStyle'>
            {memoryContent}              
            {carouselControls}
          </div>
        </div>
      )
    }else{
      return null
    }        
}

//------------------------------------------------------------------------

renderMemoryContent = () => {

  const imageZone   = this.renderImageZone()
  const storyZone   = this.renderStoryZone()
  const detailsZone = this.renderDetailsZone()

  if(this.state.activefile){
    return (
        <div className='modalrow'>

          {imageZone}
          {storyZone}
          {detailsZone}

        </div>
    )
  } else {
    return null
  }

}

//------------------------------------------------------------------------

renderKeyWords =() =>{

  if(this.state.editMode)
  {
    return (
      <ul className='wordList' > {
        this.state.keywords.map((word, idx) => {
        return (
          <li className='wordListItem' key={'k'+idx}>
            <CheckBox checkedImg = {tick} label = {word}/>
          </li> )})}
      </ul>
    )
  }else{
    return (
      <ul className='wordList' > {
