onTitleBlur = (e) => {
  const newTitle = e.target.value
  if(newTitle !== this.props.memory.title){
    Memriio.updateTitle(this.props.memory.memid,newTitle)
  }  
}
//------------------------------------------------------------------------

onDescriptionBlur = (e) => {
  const newDescription = e.target.value
  if(newDescription !== this.props.memory.description){
    Memriio.updateDescription(this.props.memory.memid,newDescription)
  }  
}
//------------------------------------------------------------------------

onStoryBlur = (e) => {
  const newStory = e.target.value
  if(newStory !== this.props.memory.story){
    Memriio.updateStory(this.props.memory.memid,newStory)
  }  
}

//------------------------------------------------------------------------

handleStoryChange = (event) =>{
  let addarray =[]
  let story = event.target.value
  let words = WordExctractor.extract(story,
                                      {
                                          language:"english",
                                          remove_digits: true,
                                          return_changed_case:true,
                                          remove_duplicates: true
                                      });
  words.map(word =>{
      if(!this.searchWordExists(word)){
        addarray.push({included:true,keyword:word,strength:0})
      }      
  }) 
  this.setState({searchWords:addarray,story:story})  
}

//------------------------------------------------------------------------

searchWordExists = (word) => {
  return this.state.searchWords.find(wordItem => word === wordItem.keyword)    
}

