import React from 'react';
import tick from '../images/checkmark.png'
import tag from '../images/tag.png'
import author from '../images/author.png'
import x from '../images/x-symbol.png'
import left from '../images/chevron-left.png'
import right from '../images/chevron-right.png'
import './memorymodal.css'

import {getAllMemoryFiles, 
  getTaggedPeople,
  getMemoryClouds,
  getMemorySearchWords, 
  getCloudPeople,
  getUser,
 } from '../memriioserver'
import {CheckBox,SubTag,ImageLabel} from '../buttons/buttons'



class MemoryModal extends React.Component{


    state ={
        memory:null,
        taggedPeople:[],
        memoryClouds:[],
        searchWords:[], 
        keywords:[],    // this is temporary   
        memfiles:null,
        cloudPeople:[],
        userClouds:[],
        activefile:null,
        author:null,
        memfileIndex:0
    }


    onHide = () =>{
      this.props.onHideModal(this.props.memory)
    }
//---------------------------------------------------------------------------------

  componentDidUpdate = (prevProps,prevState) =>{

    if(this.props.memory)
    {
      if(!prevProps.memory || this.props.memory.memid !== prevProps.memory.memid){

        this.populateMemoryFiles(this.props.memfiles)
        getUser              (this.props.memory.userid,(author => {
                                                              this.setState({author:author})
                                                              console.log('Author + ' + JSON.stringify(author))
                                                          }))
        getTaggedPeople      (this.props.memory.memid,((people)=>{this.setState({taggedPeople:people})}))
        getMemoryClouds      (this.props.memory.memid,((clouds)=>{this.populateCloudInfo(clouds)}))
        getMemorySearchWords (this.props.memory.memid,((words =>{this.populateSearchWords(words)})))



      }      
    }
  }

//---------------------------------------------------------------------------------

  getHeroFile = () => {
    this.state.state.memfileIndex=0
    let hero = this.state.memfiles[0]

    this.state.memfiles.map((memfile,index)=> {

        if(memfile.ishero){         
            this.state.memfileIndex = index            
            hero = memfile
        }
    })
    return hero
  }
//---------------------------------------------------------------------------------
  populateMemoryFiles = (memfiles) => {

    this.state.memfiles = memfiles
    this.setState({
        activefile:this.getHeroFile()
    })
  }
//---------------------------------------------------------------------------------
  populateSearchWords = (words) =>{
    this.state.keywords = []
    words.map(worditem =>{
        this.state.keywords.push(worditem.keyword)
    })
    this.setState({searchWords:words})
  }

//------------------------------------------------------------------------

  populateCloudInfo = (clouds) =>{

    this.setState({memoryClouds:clouds})
    getCloudPeople (clouds,((people)=>{this.setState({cloudPeople:people})}))   
  }

  //------------------------------------------------------------------------

  render(){


    if(this.props.show){
      let memoryContent = this.renderMemoryContent()
      let carouselControls = this.renderCarouselControls()
      return(
        <div className= 'backdropStyle'>
          <div className='modalStyle'>
            {memoryContent}              
            {carouselControls}
          </div>
        </div>
      )
    }else{
      return null
    }        
}

//------------------------------------------------------------------------

renderMemoryContent = () => {
    const mem = this.props.memory
    const keywords  = this.state.keywords.map(
        (word, idx) => {
          return (
            <li 
                className='wordListItem'
                key={'k'+idx}>
              <CheckBox 
                  checkedImg = {tick}
                  label = {word}/>
            </li>
            )
          })
    const people  = this.state.taggedPeople.map(
      p => {
          return (
            <li 
              className='wordListItem'
              key={'t' + p.userid}><ImageLabel img = {tag} label = {p.firstname+' '+p.lastname}/>                                                        
            </li>
          )
        })
    if(this.state.author){
      people.push(      
        <li key={'a' + this.state.author.userid}>
          <ImageLabel 
            img = {author}
            label = {this.state.author.firstname + ' ' + this.state.author.lastname}/>
        </li>
      ) 
    }

    const details = 
          <div>
            <li 
              className='wordListItem'
              key={'location'}><ImageLabel img = {tag} label = {this.props.memory.location}/>
            </li>
            <li 
              className='wordListItem'
              key={'sss'}><ImageLabel img = {tag} label = {this.props.memory.createdon}/>
            </li>
            </div>


    people.reverse()                                

    if(this.state.activefile){
      return (
          <div className='modalrow'>

            <div className='imgcolumn'>
              <img 
                alt='memory' 
                src={this.state.activefile.thumburl} 
                className = 'imgStyle'
              />
            </div>

            <div className = 'infoColumn'>

              <h4>{mem.title} </h4>
              <p className="f6 lh-copy i measure mt2 mid-gray">{mem.description}</p>
              <p className="f6 lh-copy measure mt2 black"> { mem.story } </p>

            </div>  

            <div className = 'detailsColumn'>

              <h4>Tags </h4>
              <ul className='wordList' > { keywords } </ul>
              <h4>People</h4>
              <ul className='wordList' > { people   } </ul>
              <h4>Details</h4>
              <ul className='wordList' > { details   } </ul>
              <div className='footer'>                        
                    <img  
                      className='closeBtn'                   
                      src = {x}
                      onClick={this.onHide} />  
              </div>              
            </div>  

          </div>
      )
    } else {
      return null
    }

  }

//------------------------------------------------------------------------

goBack = (e) => {
  if(this.state.memfiles)
  {
    let ubound = this.state.memfiles.length-1
    if(this.state.memfileIndex === 0){
      this.state.memfileIndex = ubound 
    }else{
      this.state.memfileIndex--
    }
    this.setState({activefile:this.state.memfiles[this.state.memfileIndex]})
  }
}

goForward = (e) => {
  if(this.state.memfiles)
  {
    let ubound = this.state.memfiles.length-1
    if(this.state.memfileIndex === ubound){
      this.state.memfileIndex = 0 
    }else{
      this.state.memfileIndex++
    }
    this.setState({activefile:this.state.memfiles[this.state.memfileIndex]})
  }    
}

renderCarouselControls = () => {
  if(this.state.memfiles)
  {
    const mem = this.props.memory
    const mfiles = this.state.memfiles
    const thumbs = mfiles.map((mfile,index) =>{
        return(
          <img key={'thumb'+index} className='thumb' src = {mfile.thumburl} onClick={(index) =>{}}/> 
        )})

    return (
      <div className='controlbox'>
      <img className='closeBtn' src = {left} onClick={this.goBack} /> 
      <div className='thumbArray'>
          {thumbs}
      </div> 
      <img className='closeBtn' src = {right} onClick={this.goForward} /> 
      </div>
    )
  }else{
    return null
  }
}


};  


export default MemoryModal;
