import React from 'react';
import tag from '../images/tag.png'
import cloudIMG from '../images/cloud.png'

import {ImageLabel} from '../buttons/buttons'
import SelectDropDown from 'react-dropdown-select'


//-------------------------------------------------------------------

export function peopleDropSearch ( people ,callBack){

  let peoplesNames = people.map((person,idx) => {
      return {value : idx, label: (person.firstname + ' ' + person.lastname)}})
  return (



  <SelectDropDown 

      placeholder   = '+ add'
      options       = { peoplesNames } 
      searchable    = { true }
      closeOnSelect = { true }
      clearOnSelect = { true }


      //------------

      onChange = { 
          items => {
            let index = items[0].value
            if(callBack) callBack(people[index])
          }
        }

      //------------

      itemRenderer = {({ item, itemIndex, props, state, methods }) => (

        <ImageLabel 

          key={'i' + itemIndex} 
          onClick={() => methods.addItem(item)} 
          leftImg = {tag}
          label = {item.label}
        />
      )}

  />
  )
}

//-------------------------------------------------------------------

export function cloudDropSearch ( clouds, showmulti,keepInList,callBack){

  let cloudnames = clouds.map((cloud,idx) => {
      return {value : idx, label: cloud.name}})
  return (

  <SelectDropDown 

      placeholder         = '+ add'
      multi               = { showmulti } 
      keepSelectedInList  = {keepInList}
      options             = { cloudnames } 
      searchable          = { true }
      closeOnSelect       = { true }
      clearOnSelect       = { true }


      //------------

      onChange = { 
          items => {
            let index = items[0].value
            if(callBack) callBack(clouds[index])
          }
        }

      //------------

      itemRenderer = {({ item, itemIndex, props, state, methods }) => (

        <ImageLabel 

          key={'i' + itemIndex} 
          onClick={() => methods.addItem(item)} 
          leftImg = {tag}
          label = {item.label}
        />
      )}

  />
  )
}

//-------------------------------------------------------------------
